import ExpenseItem from "./ExpenseItem";
import "./ComponentExpense.css";
import Card from "../UI/Card";
import ExpensesFilter from "../Expense Filter/ExpensesFilter";
import { useState } from "react";

const NewExpenses = (props) => {
  const [year, setYear] = useState("none");
  const filterChangeHandler = (yearSelected) => {
    setYear(yearSelected);
  };

  const filteredExpense = props.items.filter((expense) => {
    return expense.date.getFullYear() == year;
  });

  let notFound = <p style={{ color: "white" }}>No expenses found.</p>;

  if (filteredExpense.length > 0) {
    if (year === "none") {
      return props.items.map((expense) => (
        <ExpenseItem
          key={expense.id}
          title={expense.title}
          amount={expense.amount}
          date={expense.date}
        />
      ));
    } else {
      return filteredExpense.map((expense) => (
        <Card className="expenses">
          <ExpenseItem
            key={expense.id}
            title={expense.title}
            amount={expense.amount}
            date={expense.date}
          />
        </Card>
      ));
    }
  }

  return (
    <div>
      <Card className="expenses">
        <ExpensesFilter selected={year} onSelectOption={filterChangeHandler} />
        {notFound}
      </Card>
    </div>
  );
};

export default NewExpenses;
