import React, { useState } from "react";
import "./ExpenseItem.css";
import ExpenseDate from "./ExpenseDate";
import Card from "../UI/Card";
import ExpensesFilter from "../Expense Filter/ExpensesFilter";

function ExpenseItem(props) {
  const [title, newTitle] = useState(props.title);
  const handleClick = () => {
    newTitle("Updated!");
  };

  const yearChoosen = (yearValue) => {
    const [yearSelected, setYearSelected] = useState(yearValue);
  };

  return (
    <div>
      <ExpensesFilter onSelectOption={yearChoosen} />
      <Card className="expense-item">
        <ExpenseDate date={props.date} />
        <div className="expense-item__description">
          <h2>{title}</h2>
          <div className="expense-item__price">${props.amount}</div>
        </div>
        <button onClick={handleClick}>Change title</button>
      </Card>
    </div>
  );
}

export default ExpenseItem;
