import ExpenseItem from "./ExpenseItem";
import "./ComponentExpense.css";
import Card from "../UI/Card";
import ExpensesFilter from "../Expense Filter/ExpensesFilter";
import { useState } from "react";

const NewExpenses = (props) => {
  const [year, setYear] = useState("none");
  const filterChangeHandler = (yearSelected) => {
    setYear(yearSelected);
  };

  const displayHandler = () => {
    if (year === "none") {
      {
        props.items.map((expense) => (
          <ExpenseItem
            key={expense.id}
            title={expense.title}
            amount={expense.amount}
            date={expense.date}
          />
        ));
      }
    } else {
      const filteredExpenses = props.items.filter(
        (expense) => expense.date.getFullYear() === year
      );
      filteredExpenses.map((expense) => (
        <ExpenseItem
          key={expense.id}
          title={expense.title}
          amount={expense.amount}
          date={expense.date}
        />
      ));
    }
  };

  return (
    <div>
      <Card className="expenses">
        <ExpensesFilter
          selected={year}
          onSelectOption={filterChangeHandler}
          onChange={displayHandler}
        />
        {props.items.filter((expense) => expense.date.getFullYear() === year)}
        {props.items.map((expense) => (
          <ExpenseItem
            key={expense.id}
            title={expense.title}
            amount={expense.amount}
            date={expense.date}
          />
        ))}
      </Card>
    </div>
  );
};

export default NewExpenses;
