import React, { useState } from "react";
import Expense from "./components/Expenses/ComponentExpense";
import NewExpense from "./components/New Expenses/NewExpenses";

const vanillaItems = [
  {
    id: "e1",
    title: "Toilet Paper",
    amount: 94.12,
    date: new Date(2020, 7, 14),
  },
  { id: "e2", title: "New TV", amount: 799.49, date: new Date(2021, 2, 12) },
  {
    id: "e3",
    title: "Car Insurance",
    amount: 294.67,
    date: new Date(2021, 2, 28),
  },
  {
    id: "e4",
    title: "New Desk (Wooden)",
    amount: 450,
    date: new Date(2021, 5, 12),
  },
];

const App = () => {
  const [item, setItems] = useState(vanillaItems);
  const addExpenseItem = (expense) => {
    setItems((prev) => {
      return [expense, ...prev];
    });
  };

  return (
    <div>
      <NewExpense onAddExpense={addExpenseItem} />
      <Expense newTitle={item} />
      {alert({ item })};
    </div>
  );
};

export default App;
