import React, { useState, useEffect } from "react";
import ExpenseForm from "./NewForm";
import "./NewExpenses.css";
import AddNew from "./FormEspecifications";

const NewExpense = (props) => {
  const [isOpen, setIsOpen] = useState(false);

  const saveExpenseDataHandler = (callDataExpeses) => {
    const expenseData = {
      id: `e${Math.random().toString()}`,
      ...callDataExpeses,
    };
    props.onAddExpense(expenseData);
  };

  const addExpenseHandler = (state) => {
    setIsOpen(state);
    console.log(state);
  };

  if (isOpen === true) {
    return (
      <div className="new-expense">
        <ExpenseForm onSaveExpenseData={saveExpenseDataHandler} />
      </div>
    );
  } else {
    <div className="new-expense">
      <AddNew onAddExpense={addExpenseHandler} />
    </div>;
  }
};

export default NewExpense;
