    const expenseData = {
      id: `e${Math.random().toString()}`,
      ...callDataExpeses,
    };
    props.onAddExpense(expenseData);
  };

  const formOpening = (currentState) => {
    return <AddNew onAddExpense={addExpenseHandler} />;
    if (currentState === true) {
      return <ExpenseForm onSaveExpenseData={saveExpenseDataHandler} />;
    } else {
    }
  };

  return <div className="new-expense">{formOpening}</div>;
};

export default NewExpense;
